import React, { useState } from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Select, MenuItem } from '@mui/material';
import DaumPostcode from 'react-daum-postcode';
import { Dialog, DialogTitle, DialogContent } from '@mui/material';
import axios from 'axios';

function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright © '}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}


  const handleSubmit = async (event) => {
    event.preventDefault();

    const managerData = {
      mID: event.target.elements.mID.value,
      mPW: event.target.elements.mPW.value,
      mName: event.target.elements.mName.value,
      mGender: event.target.elements.mGender.value,
      mbirthDate: event.target.elements.mbirthDate.value,
      mtel: event.target.elements.mtel.value,
      mEmail: event.target.elements.mEmail.value,
      mProfileImage: event.target.elements.mProfileImage.value,
      uAdr: event.target.elements.uAdr.value,
    };

    const data = new FormData(event.currentTarget);
    const email = data.get('email');
    const password = data.get('password');

    console.log({
      email: email,
      password: password,
    });

    try {
      await axios.post('/api/manager', managerData);
      alert('회원가입이 완료되었습니다.');
    } catch (error) {
      console.error('Error creating manager: ', error);
      alert('회원가입에 실패하였습니다. 다시 시도해주세요.');
    }
  };


  const sendVerificationCode = () => {
    // TODO: Implement the logic to send the verification code to the user's phone number
    console.log('Sending verification code...');
  };


// TODO remove, this demo shouldn't need to reset the theme.

const defaultTheme = createTheme();

export default function SignUp() {

  const [profileImage, setProfileImage] = useState('');

    const handleImageChange = (e) => {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfileImage(reader.result);
      };
      reader.readAsDataURL(e.target.files[0]);
    };
  const [address, setAddress] = useState('');
    const [open, setOpen] = useState(false);

    const handleComplete = (data) => {
      let fullAddress = data.address;
      let extraAddress = '';

      if (data.addressType === 'R') {
        if (data.bname !== '') {
          extraAddress += data.bname;
        }
        if (data.buildingName !== '') {
          extraAddress += (extraAddress !== '' ? `, ${data.buildingName}` : data.buildingName);
        }
        fullAddress += (extraAddress !== '' ? ` (${extraAddress})` : '');
      }

      setAddress(fullAddress);
      setOpen(false);
    }

    const sendVerificationCode = () => {
      // TODO: Implement the logic to send the verification code to the user's phone number
      console.log('Sending verification code...');
    };


  return (
    <ThemeProvider theme={defaultTheme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            회원 가입
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={6}>
               <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="id"
                  label="아이디"
                  name="mID"
                  autoComplete="id"
                />

                <Box mt={1}>
                 <Typography color="text.secondary">
                 영문, 숫자의 조합으로 4~12자리로 입력해주세요.
                 </Typography>
                </Box>
                </Grid>
               <Grid item xs={12}>
                <TextField
                 required
                 fullWidth
                 id="name"
                 label="이름(실명)"
                 name="mName"
                 autoComplete="name"
                 />
                 <Box mt={1}>
                  <Typography color="text.secondary">
                   실명을 입력해 주세요.
                  </Typography>
                 </Box>
                </Grid>

              <Grid item xs={12}>
               <TextField
                required
                fullWidth
                id="Gender"
                label="성별"
                name="mGender"
                autoComplete="Gender"
               />
               <Box mt={1}>
                <Typography color="text.secondary">
                 성별을 입력해주세요
                </Typography>
               </Box>
              </Grid>

               <Grid item xs={12}>
                 <Grid container spacing={1}>
                   <Grid item xs={4}>
                     <Select
                       required
                       fullWidth
                       id="phonePrefix"
                       name="phonePrefix"
                       defaultValue="010"
                     >
                       <MenuItem value="010">010</MenuItem>
                       <MenuItem value="011">011</MenuItem>
                       <MenuItem value="016">016</MenuItem>
                       <MenuItem value="017">017</MenuItem>
                       <MenuItem value="018">018</MenuItem>
                       <MenuItem value="019">019</MenuItem>
                     </Select>
                   </Grid>
                   <Grid item xs={8}>
                     <TextField
                       required
                       fullWidth
                       id="tel"
                       label="휴대폰 번호"
                       name="mtel"
                       autoComplete="tel"
                     />
                   </Grid>
                 </Grid>

               </Grid>

               <Grid item xs={12}>
                 <TextField
                   required
                   fullWidth
                   name="mPW"
                   label="비밀번호"
                   type="password"
                   id="password"
                   autoComplete="new-password"
                 />
                 <Box mt={1}>
                  <Typography color="text.secondary">
                   영문,숫자를 포함한 6자 이상의 비밀번호를 입력하세요.
                  </Typography>
                 </Box>
               </Grid>

              

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="mEmail"
                  label="이메일"
                  name="email"
                  autoComplete="email"
                />
              </Grid>

              <Grid item xs={12}>
                <Box mt={1}>
                  <Button variant="contained" color="primary" onClick={() => setOpen(true)}>
                    우편번호 검색
                  </Button>
                  <Dialog open={open} onClose={() => setOpen(false)}>
                    <DialogTitle>우편번호 검색</DialogTitle>
                    <DialogContent>
                      <DaumPostcode onComplete={handleComplete} />
                    </DialogContent>
                  </Dialog>
                </Box>
                <Box mt={2}>
                  <TextField
                    required
                    fullWidth
                    id="address"
                    label="기본 주소"
                    name="uAdr"
                    value={address}
                    autoComplete="address"
                  />

                </Box>
                <Box mt={1}>
                 <TextField
                  required
                  fullWidth
                  id="address"
                  label="상세 주소"
                  name="uAdr"
                  autoComplete="address"
                 />
                </Box>

              </Grid>
              <Grid item xs={12}>
                    <input
                      accept="image/*"
                      type="file"
                      onChange={handleImageChange}
                    />
                    {profileImage && (
                      <img src={profileImage} alt="프로필 이미지" style={{ width: 100, height: 100 }} />
                    )}
                  </Grid>


            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              회원가입 하기
            </Button>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <Link href="#" variant="body2">
                  이미 아이디가 있으신가요? 로그인하러 가기
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
        <Copyright sx={{ mt: 5 }} />
      </Container>
    </ThemeProvider>
  );
}
